<?phpif (! defined("BASEPATH")) exit("No direct script access allowed");class Main extends CI_Controller{	private $usuario;	    public function __construct()	{		parent::__construct();		if($this->session->userdata('user')) $this->usuario = json_decode($this->session->userdata('user'));		else header('location:' .base_url());	}    public function index(){}	public function lista()	{		$this->load->model('Dashboard_model');		$actual = date('Y-m-d');				$venccobrar = $this->Dashboard_model->query('lista_movimientos_proveedor','numero_documento,nombre,					DATE_FORMAT(fecha_vencimiento,"%d/%m/%Y") as fecha,FORMAT(monto,2) as monto',					['idtipooperacion' => 1,'liquidado' => 0, 'DATE_FORMAT(fecha_vencimiento,"%Y-%m-%d")<' => $actual]);				echo json_encode(['data' => $venccobrar]);	}}